import React, { useState } from "react";
import Link from "next/link";

import { VideoGalleryStyle } from "./VideoGalleryStyle";
import FontButton from "../../styles/fontsStyles/fontButton";
import FontP from "../../styles/fontsStyles/fontP";
import Fonth2 from "../../styles/fontsStyles/Fonth2";

import home from "../../content/home.json"
import porjectItems from "../../content/porject.json"


const VideoGallery = props => {
    const { title10, text9 } = home;
    const [ tab, setTab ] = useState(0);
    const [ porjectItems, setPorjectItem ] = useState(porjectItems[0]);

    const changeItem = (number) => {
        setPorjectItem(porjectItems[number]);
    }

    const changeTab = (number) => {
        if(number >= 0 && number < 4) {
            setTab(number);
            changeItem(number);
        } 

    }

    return (
        <VideoGalleryStyle >
            <div className="global">
                <div className="column1">
                    <FontP color="white">{text9}</FontP>
                    <img></img>
                </div>
                <div className="title">
                        <Fonth2 color="white"> {title10}</Fonth2>
                    </div>
                    <div className="colum1">
                        <div className="text">
                        <FontP color="white">Play</FontP>
                            <FontP color="white">Play</FontP>
                        </div>
                    </div>
            </div>
            <FontButton color={props.color} size={props.size}> {props.content}</FontButton>   
        </VideoGalleryStyle>
    );
};

export default VideoGallery;
