import React, { useState } from "react";
import Link from "next/link";

import { WorkGalleryStyle } from "./WorkGalleryStyle";
import FontA from "../../styles/fontsStyles/fonta";
import FontP from "../../styles/fontsStyles/fontP";
import Fonth2 from "../../styles/fontsStyles/Fonth2";

import Button from "../Button/Button"

import home from "../../content/home.json"
import projectItems from "../../content/project.json"


const WorkGallery = props => {
    const { title10, text9 } = home;
    const [tab, setTab] = useState(0);
    const [projectItem, setProjectItems] = useState(projectItems[0]);

    const changeItem = (number) => {
        setProjectItems(projectItems[number]);
    }

    const changeTab = (number) => {
        if (number >= 0 && number < 4) {
            setTab(number);
            changeItem(number);
        }

    }
    const  displayLinks = () => {
        return links.map((link, i) => {
            const { name, url  } = link;
            return (<a key={i} target="_blank" rel="nofollow noopener noreferrer" href={url}><Button content={name}/></a>)
        })
    }

    const { img, description, client, stackDesign, stackWeb } = projectItem;

    return (
        <WorkGalleryStyle>
            <div className="global">
                <div className="img">
                    <img src={img} />
                </div>
                <div className="info">
                    <div>
                        <FontP color="white">{description}</FontP>
                        <Fonth2 color="white">{client}</Fonth2>
                        <div className="buttons">
                            {displayLinks()}
                        </div>
                    </div>
                    <div className="buttonDiv">
                        <div className={`${tab === 0 && 'active'}`} onClick={() => changeTab(0)}></div>
                        <div className={`${tab === 1 && 'active'}`} onClick={() => changeTab(1)}></div>
                        <div className={`${tab === 2 && 'active'}`} onClick={() => changeTab(2)}></div>
                        <div className={`${tab === 3 && 'active'}`} onClick={() => changeTab(3)}></div>

                    </div>




                </div>


                <div className="column1">
                    <FontA size="16px" className="play" color="white" href={url}>{text9}</FontA>
                    <img src={photo} />
                </div>
                <div className="title">
                    <Fonth2 color="white"> {title10}</Fonth2>
                </div>
                <div className="colum1">
                    <div className="text">
                        <FontP color="white">{description}</FontP>
                        <Fonth2 color="white">{name}</Fonth2>
                        <div className="buttonDiv">
                            <div className={`${tab === 0 && 'active'}`} onClick={() => changeTab(0)}></div>
                            <div className={`${tab === 1 && 'active'}`} onClick={() => changeTab(1)}></div>
                        </div>
                    </div>
                </div>
            </div>
        </WorkGalleryStyle>
    );
};

export default WorkGallery;
